<template>
  <div id="app">
    <Header
      v-bind:story="this.stories[this.$store.state.currentStoryId]"
      v-bind:stories="this.stories"
      v-bind:showHeaderBackground="this.showHeaderBackground"
      @openStory="openStory($event)"
    />
    <div id="close-nav" @click="$emit('closeNav')"></div>
    <transition
      :name="this.transition"
      mode="out-in"
      v-on:before-enter="beforeEnter()"
      v-on:after-leave="afterLeave()"
    >
      <router-view
        v-bind:stories="stories"
        @openStory="openStory($event)"
      />
    </transition>
    <div id="footer"></div>
    <v-dialog />
  </div>
</template>

<script>
import Header from '@/components/Header.vue'
import Stories from '@/stories/ciell/ciell.js'

export default {
  data () {
    return {
      stories: Stories.sort((a, b) => (a.id > b.id) ? 1 : -1),
      transition: this.$route.name === 'home' ? 'slide-left' : 'slide-right',
      showHeaderBackground: this.$route.name !== 'home'
    }
  },
  components: {
    Header
  },
  methods: {
    openStory (id) {
      if (typeof this.stories[id].placeholder !== 'undefined') {
        this.$modal.show('dialog', {
          title: 'Please Note',
          text: 'This story will be available soon.',
          buttons: [
            {
              title: 'Close'
            }
          ]
        })

        return
      }

      // Keep current chapter id alive
      // if (id !== this.$store.state.currentStoryId) {
      this.$store.commit('setCurrentChapterId', 0)
      // }

      this.$store.commit('setCurrentStoryId', id)
      this.$router.push('story')
    },
    beforeEnter () {
      this.transition = this.$route.name === 'home' ? 'slide-left' : 'slide-right'
    },
    afterLeave () {
      document.body.scrollTop = 0
      this.showHeaderBackground = (this.$route.name !== 'home')
    }
  }
}
</script>

